import styled from 'styled-components';

export const Container = styled.header`
  background: var(--blue);
`;

export const Content = styled.div`
  max-width: 1128px;
  margin: 0 auto;
  display: flex;
  flex-direction: row;
`;

export const Title = styled.h1`
  font-family: 'Gruppo', sans-serif;
  font-size: 20px;
  font-weight: 400;
  color: var(--white);
`;
